import React, { useRef, useEffect, useContext } from 'react';
import { Row, Button, Container } from 'reactstrap';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo.png';
import "./header.css";
import { AuthContext } from '../../context/AuthContext';

const nav_links = [
    {
        path: '/home',
        display: 'Home'
    },
    {
        path: '/about',
        display: 'About'
    },
    {
        path: '/tours',
        display: 'Tours'
    },
];

const Header = () => {
    const headerRef = useRef(null);
    const menuRef=useRef(null);
    const navigate = useNavigate();
    const { user, dispatch } = useContext(AuthContext);

    const logout = () => {
        dispatch({ type: 'LOGOUT' });
        navigate('/');
    };

    useEffect(() => {
        const handleScroll = () => {
            if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
                headerRef.current.classList.add('sticky__header');
            } else {
                headerRef.current.classList.remove('sticky__header');
            }
        };
        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [headerRef]);

    const toggleMenu=()=> menuRef.current.classList.toggle('show__menu')

    return (
        <header className="header" ref={headerRef}>
            <Container>
                <Row>
                    <div className="nav_wrapper d-flex align-items-center justify-content-between">
                        {/* logo start */}
                        <div className="logo">
                            <img src={logo} alt="" />
                            {/* img  end */}
                        </div>
                        {/* menu start */}
                        <div className='navigation' ref={menuRef} onClick={toggleMenu}>
                            <ul className="menu d-flex align-items-center gap-5">
                                {
                                    nav_links.map((item, index) => (
                                        <li className="nav__items" key={index}>
                                            <NavLink to={item.path} className={(navClass) => navClass.isActive ? 'active__link' : ''}>
                                                {item.display}
                                            </NavLink>
                                        </li>
                                    ))
                                }
                            </ul>
                        </div>
                        {/* menu end */}
                        <div className="nav_right d-flex align-items-center gap-4">
                            <div className="nav_btns nav_right d-flex align-items-center gap-4">
                                {
                                    user ? ( <>
                                    <h5 className='mb-0'>{user.username}</h5>
                                    <Button className='btn btn-dark' onClick={logout}>Logout</Button>
                                    </> 
                                       ) : (
                                        <>
                                            <Button className="primary__btn"><Link to='/login'>Login</Link></Button>
                                            <Button className="primary__btn" ><Link to='/register'>Register</Link></Button>
                                        </>
                                )}
                            </div>
                            <span className="mobile__menu" onClick={toggleMenu}>  
                                <i className="ri-menu-fill"></i>
                            </span>
                        </div>   
                    </div>
                </Row>
            </Container>
        </header>
    );
};

export default Header;
