import React from 'react'
import '../styles/tour-details.css'
import { Container,Row,Col,Form,ListGroup } from 'reactstrap';
import  {useParams} from 'react-router-dom'
import calculateAvgRating from '../utils/avgRating';
import avatar from '../assets/images/avatar.jpg'
import { useRef,useState } from 'react'
import Booking from '../Components/Booking/Booking';
import Newsletter from '../shared/Newsletter';
import useFetch from '../hooks/useFetch';
import { BASE_URL } from '../utils/config';


const TourDetails = () => {
  const { _id } = useParams();
  const reviewMsgRef=useRef('')
  const [tourRating,setTourRating]=useState(null)

  // fetch data from database
  const {data:tour,loading,error} = useFetch('${BASE_URL}/tours/${id}');
  useEffect(() => {
    fetchTour();
  }, []);
  //destructure properties from tour object
  const { title, city, distance, price, maxGroupSize, desc, photo, reviews,address} = tour;
  const { totalRating,avgRating } = calculateAvgRating(reviews);
  const options={day:'numeric',month:'long',year:'numeric'}
  const submitHandler = e => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;
  };
  useEffect(()=>{
    window.scrollTo(0,0)
  },[])
  return (
    <>

      <section>
        <Container>
          {
            !loading && !error && <Row>
            <Col lg='8'>
              <div className="tour__content">
                <img src={photo} alt="" />
                <div className="tour__info">
                  <h2>{title}</h2>
                  <div className="d-flex align-items-center gap-5">
                  <span className='tour__rating d-flex align-items-center gap-1'>
                    <i class="ri-star-s-line" style={{'color':"var(--secondary-color)"}}></i>{avgRating===0 ? null : avgRating}
                    {totalRating===0 ? ('Not rated') : (<span>({reviews?.length})</span>)}
                  </span>
                  <span className='tour__location d-flex align-items-center gap-1'>
                  <i class="ri-map-pin-user-line"></i>{address}
                  </span>
                  </div>
                  <div className="tour__extra-details">
                    <span><i class="ri-map-pin-2-line"></i>{city}</span>
                    <span><i class="ri-money-dollar-circle-line"></i>${price}per person</span>
                    <span><i class="ri-map-pin-time-line"></i>{distance}km</span>
                    <span><i class="ri-group-fill"></i>{maxGroupSize}</span>
                  </div>
                    <h5>Description</h5>
                    <p>{desc}</p>
                </div>
                  {/* =============tour review section start======================== */}
                  <div className="tour__reviews mt-4"> 
                    <h4>Reviews({reviews?.length}reviews)</h4>
                    <Form onSubmit={submitHandler}>
                      <div className='d-flex align-items-center gap-3 mb-4 rating__group'>
                        <span onClick={()=>setTourRating(1)}>1<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i></span>
                        <span onClick={()=>setTourRating(2)}>2<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i></span>
                        <span onClick={()=>setTourRating(3)}>3<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i></span>
                        <span onClick={()=>setTourRating(4)}>4<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i></span>
                        <span onClick={()=>setTourRating(5)}>5<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i></span>
                      </div>
                      <div className="review__input">
                        <input type="text" ref={reviewMsgRef} placeholder='Shere Your Thoughts' required />
                        <button className='btn primary__btn text-white' type='submit' >Submit</button>
                      </div>
                    </Form>
                    <ListGroup className="user__reviews">
                      {
                        reviews?.map(review=>(
                          <div className="review__item">
                            <img src={avatar} alt=''/>
                            <div className="w-100">
                              <div className="d-flex align-items-center justify-content-between">
                                <div>
                                  <h5>Karthikeya</h5>
                                  <p>{new Date('08-12-2024').toLocaleDateString('en-US',options)}</p>
                                </div>
                                <span className='f-flex align-items-center'>
                                  5<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i>
                                </span>
                              </div>
                              <h6>Amazing Tour</h6>
                            </div>
                            <img src={avatar} alt=''/>
                            <div className="w-100">
                              <div className="d-flex align-items-center justify-content-between">
                                <div>
                                  <h5>Karthikeya</h5>
                                  <p>{new Date('08-12-2024').toLocaleDateString('en-US',options)}</p>
                                </div>
                                <span className='f-flex align-items-center'>
                                  5<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i>
                                </span>
                              </div>
                              <h6>Amazing Tour</h6>
                            </div>
                            <img src={avatar} alt=''/>
                            <div className="w-100">
                              <div className="d-flex align-items-center justify-content-between">
                                <div>
                                  <h5>Karthikeya</h5>
                                  <p>{new Date('08-12-2024').toLocaleDateString('en-US',options)}</p>
                                </div>
                                <span className='f-flex align-items-center'>
                                  5<i class="ri-star-s-line" style={{'color':"var(--heading-color"}}></i>
                                </span>
                              </div>
                              <h6>Amazing Tour</h6>
                            </div>
                          </div>
                        ))
                      }

                    </ListGroup>
                  </div>
                  {/* =============tour review section end======================== */}
              </div>
            </Col>
            <Col lg='4'>
              <Booking tour={tour} avgRating={avgRating}/>     
            </Col>
          </Row>
          }
        </Container>
      </section>
      <Newsletter/>
    </>

  )
};

export default TourDetails;
