import React, { useState, useEffect } from 'react';
import TourCard from '../../shared/TourCard';
import { Col } from 'reactstrap';
import useFetch from '../../hooks/useFetch';
import { BASE_URL } from '../../utils/config';
import Pagination from './Pagination';

const FeaturedTourList = () => {
  const [pageCount, setPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);

  const { data: featuredTours, loading, error } = useFetch(`${BASE_URL}/tours/search/getFeaturedTours?page=${currentPage}&limit=8`);
  const { data: tourCount } = useFetch(`${BASE_URL}/tours/search/getTourCount`);

  useEffect(() => {
    const pages = Math.ceil(tourCount / 8);
    setPageCount(pages);
  }, [tourCount]);

  const onPageChange = (page) => {
    setCurrentPage(page);
  };

  if (loading) {
    return <h4 className='text-center'>Loading.......</h4>;
  }

  if (error) {
    return <h4 className='text-center'>Error: {error.message}</h4>;
  }

  return (
    <div>
      <Pagination pageCount={pageCount} currentPage={currentPage} onPageChange={onPageChange} />
      <Row>
        {featuredTours.map((tour) => (
          <Col lg='3' md='6' className='mb-4' key={tour._id}>
            <TourCard tour={tour} pageNumber={currentPage * 8 + featuredTours.indexOf(tour) + 1} />
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default FeaturedTourList;
