import React,{useRef,useEffect} from 'react'
import {Row,Button, Container} from 'reactstrap'
import { NavLink,Link } from 'react-router-dom' 
import logo from '../../assets/images/logo.png'
import "./header.css"
const nav__links=[
    {
        path:'/home',
        display:'Home'
    },
    {
        path:'/about',
        display:'About'
    },
    {
        path:'/tours',
        display:'Tours'
    },
]


const Header = () => {
    const headerRef=useRef(null)
    const stickyHeaderFunc =() =>{
        window.addEventListener('scroll',()=>{
            if(document.body.scrollTop>80||document.documentElement.scrollTop>80){
                headerRef.current.classList.add('sticky__header')
            }else{
                headerRef.current.classList.remove('sticky__header')
            }
        })
    }

useEffect(()=>{
    stickyHeaderFunc()

    return window.removeEventListener('scroll',stickyHeaderFunc)
})

  return (
     <header className="header" ref={headerRef}>
        <Container>
            <Row>
                <div className="nav_wrapper d-flex align-items-center justify-content-between">
                    {/* logo start */}
                    <div className="logo">
                        <img src={logo} alt=''></img>
                        {/* img  end */}
                    </div>
                    {/* menu start */}
                    <div className='Navigation'>
                        <ul className="menu d-flex align-items-center gap-5">
                            { 
                                nav__links.map((item,index)=>(
                                    <li className="nav__items" key={index}>
                                        <NavLink to={item.path} className={navclass=> navclass.isActive ? 'active__link': " "}>
                                            {item.display}
                                        </NavLink>
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    {/* menu end */}
                    <div className="nav_right d-flex align-item-center gap-4">
                        <div className="nav_btns nav_right d-flex align-item-center gap-4">
                            <Button className=" primary__btn"><Link to='/login'>Login</Link></Button>
                            <Button className=" primary__btn" ><Link to='/register'>Register</Link></Button>
                        </div>
                        <span className="mobile__menu">  
                            <i class="ri-menu-fill"></i>
                        </span>
                    </div>   
                </div>
            </Row>
        </Container>
     </header>
  )
};

export default Header;
